loadData();

btnAction = "Insert";


$(".modal-category").on("submit",function(event) {

    event.preventDefault();

    let name = $("#name").val();
    let role = $("#role").val();
    let icon = $("#icon").val();
    let id = $("#update_id").val();

    let sendingData = {}

    if(btnAction == "Insert"){
        sendingData = {
            "name" : name,
            "role" : role,
            "icon" : icon,
            "action" : "register_category"
            
        }
    
    }else{
          sendingData = {
            "id" : id,
            "name" : name,
            "role" : role,
            "icon" : icon,
            "action" : "update_category"
            
        }
    
    }

    
  
    $.ajax( {
        method: "POST",
        dataType: "JSON",
        url : "../API/category.php",
        data : sendingData,
        success: function(data){

            let status = data.status;
            let response = data.data;

            if(status){

                Swal.fire({
                    title: response,
                    icon: "success"
                  });

                console.log("success",response);
                btnAction = "Insert";
                loadData();
            }else{
                alert("error",response);
            }
           

        },
        error: function(data){
          console.log("error",data);
        }
    })

})



function loadData(){

    $("#Category-Table tbody").html('');

    let sendingData = {
        "action" : "read_all_category"
    }

    $.ajax( {
        method: "POST",
        dataType: "JSON",
        url : "../API/category.php",
        data : sendingData,
        success: function(data){

            let status = data.status;
            let response = data.data;
            let html = '';
            let tr = '';

            if(status){

                response.forEach( itm => {

                    tr += "<tr>";
                    for(let i in itm){

                      
                            tr += `<td>${itm[i]}</td>`;
                        }

                    

                        tr += `<td > <buttonclass="btn_upda"> <a class="updete_cl"update_id=${itm['id']}><span class="material-symbols-outlined" style ="color:#fff;background-color:var(--base-clr); font-size: xx-large;  font-weight: bold; border-radius: 5px; ">edit
                        </span></a></button> &nbsp;&nbsp;<a class="delete_cl" delete_id=${itm['id']}><span class="material-symbols-outlined" style ="color:#fff;background-color:red;font-size: xx-large;  font-weight: bold; border-radius: 5px;">delete
                        </span></a> </td>` 
                       
                        tr += '</tr>'  

                })

                $("#Category-Table tbody").append(tr);

            }else{
                console.log("error",response);
            }
           

        },
        error: function(data){
            console.log(data);

        }
    })

}

