$("#modal-depart").on("submit",function(event) {

    event.preventDefault();

    let name = $("#department_name").val();
    let id = $("#updateid").val();
   

    let sendingData = {}

    if(btnAction == "Insert"){
        sendingData = {
          
            "department_name" : name,
            "action" : "register_department"
            
        }
    
    }else{
          sendingData = {
           
            "department_id" : id,
            "department_name" : name,
            "action" : "Update_department"
            
        }
    
    }

    
  
    $.ajax( {
        method: "POST",
        dataType: "JSON",
      
        url : "../APi/department.php",
        data : sendingData,
        success: function(data){

            let status = data.status;
            let response = data.data;

            if(status){

                Swal.fire({
                    title: response,
                    icon: "success"
                  });

                  setTimeout(() => {
                   
                    $(".modal-depart").show();
                  }, 1000);

                console.log("success",response);
                btnAction = "Insert";
                loadData();
            }else{
                alert("error",response);
            }
           

        },
        error: function(data){
          console.log("error",data);
        }
    })

})


loadData();
function loadData(){

    $("#depart_Table tbody").html('');

    let sendingData = {
        "action" : "department_read"
    }

    $.ajax( {
        method: "POST",
        dataType: "JSON",
        url : "../APi/department.php",
        data : sendingData,
        success: function(data){

            let status = data.status;
            let response = data.data;
            let html = '';
            let tr = '';

            if(status){

                response.forEach( itm => {

                    tr += "<tr>";
                    for(let i in itm){

                      
                            tr += `<td>${itm[i]}</td>`;
                        }

                    

                        tr += `<td > <buttonclass="btn_upda"> <a class="updete_cl"update_id=${itm['department_id']}><span class="material-symbols-outlined" style ="color:#fff;background-color:var(--base-clr); font-size: xx-large;  font-weight: bold; border-radius: 5px; ">edit
                        </span></a></button> &nbsp;&nbsp;<a class="delete_cl" delete_id=${itm['department_id']}><span class="material-symbols-outlined" style ="color:#fff;background-color:red;font-size: xx-large;  font-weight: bold; border-radius: 5px;">delete
                        </span></a> </td>` 
                       
                        tr += '</tr>'  

                })

                $("#depart_Table tbody").append(tr);

            }else{
                console.log("error",response);
            }
           

        },
        error: function(data){
            console.log(data);

        }
    })

}



function department_fetch(id){

    let sendingData = {
        "action" : "department_fetch",
        "department_id": id
    }

    $.ajax( {
        method: "POST",
        dataType: "JSON",
     
        url : "../APi/department.php",
        data : sendingData,
        success: function(data){

            let status = data.status;
            let response = data.data;
            let html = '';
            let tr = '';

            if(status){
            
                btnAction = "Update";
                   $("#updateid").val(response['department_id']);
                   $("#department_name").val(response['department_name']);
                  
                
                   
               
                   $("#modal-depart").show();

            }else{
                alet("error",response);
            }
           

        },
        error: function(data){

        }
    })



}





function department_delete(id){

    let sendingData = {
        "action" : "delete_departments_info",
        "department_id": id
    }

    $.ajax( {
        method: "POST",
        dataType: "JSON",
        url : "../APi/department.php",
      
        data : sendingData,
        success: function(data){

            let status = data.status;
            let response = data.data;
            let html = '';
            let tr = '';

            if(status){

              
                Swal.fire({
                    title: response,
                    icon: "success"
                  });

                loadData();

            }else{
                alert(response);
            }
           

        },
        error: function(data){

        }
    })



}







