$("#modal-facult").on("submit",function(event) {
    event.preventDefault();
    let f_id = $("#update_id").val();
    let names = $("#name").val();
     let department_id = $("#department_id").val();
   
   

    let sendingData = {}

    if(btnAction == "Insert"){
        sendingData = {
            "name" : names,
             "department_id" :department_id,
            "action" : "register_Facult"
            
        }
    }
    else{
          sendingData = {
            "faculty_id": f_id,
            "name" : names,
            "department_id" : department_id,
            "action" : "update_facult"
          

        }
    
    }

    
  
    $.ajax( {
        method: "POST",
        dataType: "JSON",
        url : "../APi/facult.php",
        data : sendingData,
        success: function(data){

            let status = data.status;
            let response = data.data;

            if(status){

                Swal.fire({
                    title: response,
                    icon: "success"
                  });

                console.log("success",response.data);
                btnAction = "Insert";
                loadData();
            }else{
                alert("error",response);
            }
           

        },
        error: function(data){
          console.log("error",data);
        }
    })

})















