function Department_name(){

   

    let sendingData = {
        "action" : "department_read"
    }

    $.ajax( {
        method: "POST",
        dataType: "JSON",
        url : "../APi/department.php",
        data : sendingData,
        success: function(data){

            let status = data.status;
            let response = data.data;
            let html = '';
            let tr = '';

            if(status){

                
                response.forEach( res => {

                   html += `<option value="${res['department_id']}">${res['department_name']}</option>`;
                })

                $("#department_id").append(html);

              
            }else{
                alert("error",response);
            }
           

        },
        error: function(data){

        }
    })

}



loadData();
function loadData(){

    $("#facult_Table tbody").html('');

    let sendingData = {
        "action" : "facult_read"
    }

    $.ajax( {
        method: "POST",
        dataType: "JSON",
        url : "../APi/facult.php",
       
        data : sendingData,
        success: function(data){

            let status = data.status;
            let response = data.data;
            let html = '';
            let tr = '';

            if(status){

                response.forEach( itm => {

                    tr += "<tr>";
                    for(let i in itm){

                      
                            tr += `<td>${itm[i]}</td>`;
                        }

                    

                        tr += `<td > <buttonclass="btn_upda"> <a class="updete_cl"update_id=${itm['faculty_id']}><span class="material-symbols-outlined" style ="color:#fff;background-color:blue; font-size: xx-large;  font-weight: bold; border-radius: 5px; ">edit
                        </span></a></button> &nbsp;&nbsp;<a class="delete_cl" delete_id=${itm['faculty_id']}><span class="material-symbols-outlined" style ="color:#fff;background-color:red;font-size: xx-large;  font-weight: bold; border-radius: 5px;">delete
                        </span></a> </td>` 
                       
                        tr += '</tr>'  

                })

                $("#facult_Table tbody").append(tr);

            }else{
                console.log("error",response);
            }
           

        },
        error: function(data){
            console.log(data);

        }
    })

}







