const stats = {
    Strength: 10,
    Dexterity: 18,
    Constitution: 12,
    Intelligence: 15,
    Wisdom: 14,
    Charisma: 16
};

const chartContainer = document.getElementById('chart');

function drawChart() {
    chartContainer.innerHTML = ''; // Clear previous bars
    for (const [attribute, value] of Object.entries(stats)) {
        const bar = document.createElement('div');
        bar.className = 'bar';
        bar.style.height = `${(value / 20) * 100}%`; // Scale to 100%
        bar.title = `${attribute}: ${value}`;
        bar.innerHTML = `<span class="label">
        ${attribute}</span>`;
        chartContainer.appendChild(bar);
    }
}

// Update the stats with random values on button click
document.getElementById('updateButton').addEventListener('click', () => {
    for (const key in stats) {
        stats[key] = Math.floor(Math.random() * 20) + 1; // Random value between 1 and 20
    }
    drawChart();
});

// Initial chart drawing
drawChart();
