$("#linkForm").on("submit",function(event) {

    event.preventDefault();

    let name = $("#name").val();
    let link_id = $("#link_id").val();
    let category_id = $("#category_id").val();
    let id = $("#update_id").val();

    let sendingData = {}

    if(btnAction == "Insert"){
        sendingData = {
            "name" : name,
            "link" : link_id,
            "category" : category_id,
            "action" : "register_link"
            
        }
    
    }else{
          sendingData = {
            "id" : id,
            "name" : name,
            "link" : link_id,
            "category" : category_id,
            "action" : "update_link"
            
        }
    
    }

    $.ajax( {
        method: "POST",
        dataType: "JSON",
        url : "../API/system_links.php",
        data : sendingData,
        success: function(data){

            let status = data.status;
            let response = data.data;

            if(status){

                alert("success",response);
                btnAction = "Insert";
                loadData();
            }else{
                alert("error",response);
            }
           

        },
        error: function(data){
            alert("error",data.responseText);
        }
    })

})



function loadData(){

    $("#linkTable tbody").html('');

    let sendingData = {
        "action" : "read_all_db_links"
    }

    $.ajax( {
        method: "POST",
        dataType: "JSON",
        url : "../api/system_links.php",
        data : sendingData,
        success: function(data){

            let status = data.status;
            let response = data.data;
            let html = '';
            let tr = '';

            if(status){

                response.forEach( res => {

                    tr += "<tr>";
                    for(let r in res){

                      
                            tr += `<td>${res[r]}</td>`;
                        }

                    

                    tr += `<td> <a class="btn btn-info update_info" update_id=${res['id']}><i class="fas fa-edit" style="color:#fff;"></i></a>&nbsp;&nbsp;<a class="btn btn-danger delete_info" delete_id=${res['id']}><i class="fas fa-trash" style="color:#fff;"></i></a> </td>`;
                    tr += "</tr>";

                })

                $("#linkTable tbody").append(tr);

            }else{
                alert("error",response);
            }
           

        },
        error: function(data){

        }
    })

}




